import axios from "axios"
import { useState, useEffect } from 'react'
import Display from "./components/Display"
import dataUtil from "./service/dataUtil"

const App = () => {
  const baseURL = "http://localhost:3001/persons"

  const [persons, setPersons] = useState([])
  const [filteredPersons, setFilteredPersons] = useState([])
  const [onFilter, setOnFilter] = useState(false)
  const [filter, setFilter] = useState('')
  const showPersons = onFilter ? filteredPersons : persons

  const [newName, setNewName] = useState('')
  const [newPhone, setNewPhone] = useState('')

  useEffect(() => {
    dataUtil.getAll()
    .then( allpeople => {
      setPersons(allpeople)
    })
  }, [])

  const handleSubmit = (event) => {
    event.preventDefault()
    if (persons.some(x => x.name === newName)) {
      if (windows.confirm(`${newName} already exists, are you sure you want to replace the number`)) {
        const id = persons.find(x => x.name==newName).id
        dataUtil.update(id, {id: id, name: newName, number: newPhone})
        .then(person => {
          setPersons(persons.concat(person))
          setNewPhone('')
          setNewName('')
        })
      }
    } else {
      dataUtil.add({name: newName, number: newPhone})
        .then(person => {
          setPersons(persons.concat(person))
          setNewPhone('')
          setNewName('')
        })
    }
  }

  const handleChange = (setter) => (event) => {
    setter(event.target.value)
  }

  const handleFilter = (event) => {
    const newFilter = event.target.value
    if (newFilter === '') {
      setOnFilter(false)
    } else {
      setOnFilter(true)
      setFilteredPersons(persons.filter(person => person.name.toLowerCase().startsWith(newFilter.toLowerCase())))
    }
    setFilter(newFilter)
  }

  const handleDelete = (id) => (e) => {
    if (window.confirm("Are you sure you want to delete")) {
        dataUtil.del(id)
        .then(r => {
            dataUtil.getAll().then((people) => {
              setPersons(people)
            })
        }).catch(() => {
            alert("Name doesn't exist")
        })
      }
    }

  return (
    <div>
      <h2>Phonebook</h2>
      <div>Filter shown with <input value={filter} onChange={handleFilter}/></div>
      <h2>Add a new</h2>
      <form onSubmit={handleSubmit}>
        <div>
          Name: <input value={newName} onChange={handleChange(setNewName)}/>
        </div>
        <div>
          Phone: <input value={newPhone} onChange={handleChange(setNewPhone)}/>
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <Display phonebook={showPersons} handleDelete={handleDelete}/>

    </div>
  )
}

export default App
