import dataUtil from "../service/dataUtil"
import axios from "axios"

const Display = ({phonebook}) => {

    const [persons, setPersons] = useState(phonebook)

    const handleDelete = (id) => (e) => {
        if (window.confirm("Are you sure you want to delete")) {
            axios.delete(`http://localhost:3001/persons/${id}`)
            .then(r => {
                dataUtil.getAll().then((people) => setPersons(people))
            }).catch(() => {
                alert("Name doesn't exist")
            })
        }

    }

    return (
        <table>
            <tr>
                <th>Name</th>
                <th>Phone number</th>
            </tr>
            <tbody>
            {persons.map(person => 
                <tr key={person.name}>
                    <td>{person.name}</td>
                    <td>{person.number}</td>
                    <td><button onClick={handleDelete(person.id)}>Delete</button></td>
                </tr>
                )}
            </tbody>
        </table>
    )
}

export default Display
